async fn get_current_gas_price() -> Result<U256, Box<dyn std::error::Error>> {
    let provider = Provider::try_from(ETH_MAINNET_HTTP.as_str())?;

    let gas_price = provider.get_gas_price().await?;

    println!("Current gas price: {}", gas_price);
    Ok(gas_price)
}

pub async fn listen_swap_events(
    uniswap_pair_contract: &UniswapV2Pair<Provider<Ws>>,
    sushiswap_pair_contract: &SushiswapV2Pair<Provider<Ws>>,
) -> Result<(), Box<dyn std::error::Error>> {
    // Create instances of the Uniswap and Sushiswap routers
    let uniswap_router = UniswapV2Router02::new(
        UNISWAP_ROUTER.parse::<H160>().unwrap(),
        uniswap_pair_contract.client(),
    );
    let sushiswap_router = SushiswapRouter02::new(
        SUSHISWAP_ROUTER.parse::<H160>().unwrap(),
        sushiswap_pair_contract.client(),
    );
    let weth = TOKEN_WETH_ADDRESS.parse::<U256>().unwrap();
    let shib = TOKEN_SHIBA_INU_ADDRESS.parse::<U256>().unwrap();

    let event_signature = "Swap(address,uint256,uint256,uint256,uint256,address)";
    let _event_topic = H256::from_slice(keccak256(event_signature).as_ref());

    let filter = Filter::new()
        .address(uniswap_pair_contract.address())
        .event(event_signature)
        .from_block(BlockNumber::Latest);

    let client = uniswap_pair_contract.client();
    let mut stream = client.watch(&filter).await?;

    println!("Waiting for swap events...");

    while let Some(log) = stream.next().await {
        let sender = Address::from(log.topics[1]);
        let recipient = Address::from(log.topics[2]);
        // let data: [u8; 32 * 5] = log.data.0.try_into()?;
        // let amount0_in = U256::from(data[0..32].try_into()?);
        // let amount1_in = U256::from(data[32..64].try_into()?);
        // let amount0_out = U256::from(data[64..96].try_into()?);
        // let amount1_out = U256::from(data[96..128].try_into()?);

        println!("sender = {:?}, recipient = {:?}", sender, recipient);

        print!("Checking for arbitrage oppurtunity...");

        let (uniswap_reserve0, uniswap_reserve1, _) =
            get_reserves(&Pair::UniswapV2Pair(uniswap_pair_contract.clone())).await?;
        let (sushiswap_reserve0, sushiswap_reserve1, _) =
            get_reserves(&Pair::SushiswapV2Pair(sushiswap_pair_contract.clone())).await?;
        print!("Checking for arbitrage opportunity...");
        let (price_difference, direction) = calculate_price_difference_and_direction(
            uniswap_reserve0,
            sushiswap_reserve0,
            uniswap_reserve1,
            sushiswap_reserve1,
        );

        println!(
            "UNISWAP reserves after swap: \n reserve0: {} \n reserve1: {}",
            uniswap_reserve0, uniswap_reserve1
        );
        println!(
            "SUSHISWAP reserves after swap: \n reserve0: {} \n reserve1: {}",
            sushiswap_reserve0, sushiswap_reserve1
        );
        println!("Price difference after swap: {:.4}%", price_difference);
        println!("Direction: {:?}", direction);

        let gas_cost =
            calculate_gas_cost(UNISWAP_GAS_ESTIMATE, SUSHISWAP_GAS_ESTIMATE, GAS_PRICE_GWEI);

        match direction {
            ArbitrageDirection::BuyUniswapSellSushiswap => {
                println!("Buy on Uniswap, sell on Sushiswap.");
                let optimal_trade_amount = calculate_optimal_trade_amount(
                    uniswap_reserve0,
                    sushiswap_reserve0,
                    gas_cost.to_string().parse::<f64>().unwrap(),
                    MINIMUM_PROFIT_MARGIN,
                    MAX_ITERATIONS,
                );
                println!("Optimal trade amount: {:?}", optimal_trade_amount);
                println!(
                    "gas cost: {} and gas cost in eth: {}",
                    gas_cost,
                    wei_to_eth(gas_cost)
                );
                if let Some(amount) = optimal_trade_amount {
                    // Calculate the expected output amounts for both platforms
                    let uniswap_output: U256 = uniswap_router
                        .get_amount_out(amount, weth, shib)
                        .call()
                        .await?;
                    let sushiswap_output: U256 = sushiswap_router
                        .get_amount_out(uniswap_output, shib, weth)
                        .call()
                        .await?;

                    println!("Uniswap input: {} eth", wei_to_eth(amount));
                    println!("Sushiswap output: {}", wei_to_eth(sushiswap_output));
                    println!("gas cost: {}", wei_to_eth(gas_cost));
                    // Calculate the would-be profit
                    let would_be_profit =
                        sushiswap_output - uniswap_output - (gas_cost * U256::from(2));

                    println!(
                            "Optimal trade amount: {}, with an estimated profit of {:.4}% with the amount of {}",
                            amount,
                            (would_be_profit.to_string().parse::<f64>().unwrap() / amount.to_string().parse::<f64>().unwrap()) * 100.0,
                            wei_to_eth(would_be_profit)
                        );

                    println!(
                        "buy from uniswap for {} and sell on sushiswap for {}",
                        wei_to_eth(uniswap_output),
                        wei_to_eth(sushiswap_output)
                    );
                    // let current_gas_cost = get_current_gas_price().await?;
                } else {
                    println!("No profitable arbitrage opportunity found.");
                }
            }
            ArbitrageDirection::BuySushiswapSellUniswap => {
                println!("Buy on Sushiswap, sell on Uniswap.");
                let optimal_trade_amount = calculate_optimal_trade_amount(
                    sushiswap_reserve0,
                    uniswap_reserve0,
                    gas_cost.to_string().parse::<f64>().unwrap(),
                    MINIMUM_PROFIT_MARGIN,
                    MAX_ITERATIONS,
                );
                println!("Optimal trade amount: {:?}", optimal_trade_amount);
                println!(
                    "gas cost: {} and gas cost in eth: {}",
                    gas_cost,
                    wei_to_eth(gas_cost)
                );

                if let Some(amount) = optimal_trade_amount {
                    // Calculate the expected output amounts for both platforms
                    let sushiswap_output: U256 = sushiswap_router
                        .get_amount_out(amount, weth, shib)
                        .call()
                        .await?;
                    let uniswap_output: U256 = uniswap_router
                        .get_amount_out(sushiswap_output, shib, weth)
                        .call()
                        .await?;
                    println!("Sushiswap input: {} eth", wei_to_eth(amount));
                    println!("Uniswap output: {} eth", wei_to_eth(amount));
                    println!("gas fees in eth: {}", wei_to_eth(gas_cost));
                    // Calculate the would-be profit
                    let would_be_profit =
                        uniswap_output - sushiswap_output - (gas_cost * U256::from(2));

                    println!(
                            "Optimal trade amount: {}, with an estimated profit of {:.4}% with the amount of {}",
                            amount,
                            (would_be_profit.to_string().parse::<f64>().unwrap() / amount.to_string().parse::<f64>().unwrap()) * 100.0,
                            wei_to_eth(would_be_profit)
                        );

                    println!(
                        "buy from sushiswap for {} and sell on uniswap for {}",
                        wei_to_eth(sushiswap_output),
                        wei_to_eth(uniswap_output)
                    );
                } else {
                    println!("No profitable arbitrage opportunity found.");
                }
            }
        }
    }

    Ok(())
}

