pub async fn get_sushiswap_pair_address<M: Middleware>(
    factory_contract: &SushiswapV2Factory<Provider<Ws>>,
    token_a: &str,
    token_b: &str,
) -> Result<Address, Box<dyn Error + Send + Sync>> {
    let pair_address: Address = factory_contract
        .get_pair(Address::from_str(token_a)?, Address::from_str(token_b)?)
        .call()
        .await?;

    Ok(pair_address)
}
pub async fn get_uniswap_pair_address<M: Middleware>(
    factory_contract: &UniswapV2Factory<Provider<Ws>>,
    token_a: &str,
    token_b: &str,
) -> Result<Address, Box<dyn Error + Send + Sync>> {
    let pair_address: Address = factory_contract
        .get_pair(Address::from_str(token_a)?, Address::from_str(token_b)?)
        .call()
        .await?;

    Ok(pair_address)
}

pub async fn get_uniswap_pair_contract<M: Middleware>(
    provider: Arc<M>,
    pair_address: Address,
) -> UniswapV2Pair<M> {
    UniswapV2Pair::new(pair_address, provider)
}

pub async fn get_sushiswap_pair_contract<M: Middleware>(
    provider: Arc<M>,
    pair_address: Address,
) -> SushiswapV2Pair<M> {
    SushiswapV2Pair::new(pair_address, provider)
}

pub enum Pair<M: Middleware> {
    UniswapV2Pair(UniswapV2Pair<M>),
    SushiswapV2Pair(SushiswapV2Pair<M>),
}

pub async fn get_reserves<M: Middleware + 'static>(
    pair_contract: &Pair<M>,
) -> Result<(U256, U256, u32), Box<dyn std::error::Error>> {
    let (reserve0, reserve1, block_timestamp_last) = match pair_contract {
        Pair::UniswapV2Pair(uniswap_pair) => uniswap_pair.get_reserves().call().await?,
        Pair::SushiswapV2Pair(sushiswap_pair) => sushiswap_pair.get_reserves().call().await?,
    };
    Ok((
        U256::from(reserve0),
        U256::from(reserve1),
        block_timestamp_last,
    ))
}

#[derive(Debug)]
enum ArbitrageDirection {
    BuyUniswapSellSushiswap,
    BuySushiswapSellUniswap,
}

fn calculate_price_difference_and_direction(
    uniswap_reserve0: U256,
    sushiswap_reserve0: U256,
    uniswap_reserve1: U256,
    sushiswap_reserve1: U256,
) -> (f64, ArbitrageDirection) {
    let uniswap_price = uniswap_reserve1.to_string().parse::<f64>().unwrap()
        / uniswap_reserve0.to_string().parse::<f64>().unwrap();
    let sushiswap_price = sushiswap_reserve1.to_string().parse::<f64>().unwrap()
        / sushiswap_reserve0.to_string().parse::<f64>().unwrap();

    let price_difference = ((sushiswap_price - uniswap_price) / uniswap_price) * 100.0;

    let direction = if price_difference > 0.0 {
        ArbitrageDirection::BuyUniswapSellSushiswap
    } else {
        ArbitrageDirection::BuySushiswapSellUniswap
    };

    (price_difference.abs(), direction)
}

pub fn calculate_price_difference(
    reserve0_a: U256,
    reserve1_a: U256,
    reserve0_b: U256,
    reserve1_b: U256,
) -> f64 {
    let price_a = reserve1_a.to_string().parse::<f64>().unwrap()
        / reserve0_a.to_string().parse::<f64>().unwrap();
    let price_b = reserve1_b.to_string().parse::<f64>().unwrap()
        / reserve0_b.to_string().parse::<f64>().unwrap();
    let price_difference = ((price_a - price_b) / price_b) * 100.0;
    price_difference
}

pub fn calculate_gas_cost(uni_gas: u64, sushi_gas: u64, gas_price_gwei: u64) -> U256 {
    let total_gas = uni_gas + sushi_gas;
    let total_gas_cost_ether = total_gas as f64 * gas_price_gwei as f64 * 1e-9; // Convert to Ether
    let total_gas_cost_wei = (total_gas_cost_ether * 1e18) as u64; // Convert to Wei
    U256::from(total_gas_cost_wei)
}

/*
You may also consider other optimization techniques, such as golden-section search or the Newton-Raphson method, to find the optimal trade amount.
Additionally, you can look into querying an Ethereum node to get the current gas prices for more accurate estimates. This can be done using eth_gasPrice JSON-RPC method,
 or by utilizing the web3 or ethers libraries if you integrate your Rust code with JavaScript or other languages that support these libraries. */
pub fn calculate_optimal_trade_amount(
    reserve0: U256,
    reserve1: U256,
    gas_cost: f64,
    min_profit_margin: f64,
    max_iterations: u32,
) -> Option<U256> {
    // Convert reserves to f64
    let reserve0_f64 = reserve0.to_string().parse::<f64>().unwrap();
    let reserve1_f64 = reserve1.to_string().parse::<f64>().unwrap();

    // Calculate the constant product invariant, k
    let k = reserve0_f64 * reserve1_f64;

    // Define the search space
    let mut lower_bound: f64 = 0.0;
    let mut upper_bound: f64 = reserve0_f64;
    let mut optimal_trade_amount = None;
    let mut max_profit = 0.0;

    for _ in 0..max_iterations {
        let trade_amount = (lower_bound + upper_bound) / 2.0;
        let new_reserve0 = reserve0_f64 + trade_amount;
        let new_reserve1 = k / new_reserve0;

        // Calculate the slippage
        let slippage = 1.0 - (reserve1_f64 / new_reserve1);

        // Calculate the profit
        let profit = (new_reserve1 - reserve1_f64) * slippage - gas_cost;

        // Check if the trade meets the minimum profit margin and has a higher profit than the current max profit
        if profit / new_reserve1 >= min_profit_margin && profit > max_profit {
            max_profit = profit;
            optimal_trade_amount = Some(U256::from(trade_amount as u64));

            // Narrow the search space
            lower_bound = trade_amount;
        } else {
            upper_bound = trade_amount;
        }
    }

    optimal_trade_amount
}

fn wei_to_eth(wei_val: U256) -> f64 {
    let res = wei_val.as_u128() as f64;
    res / 1_000_000_000_000_000_000.0
}

